from common import *def AddPlatformSpecificVars(vars):    gen_anon = 'anon_user_id' if vars['source']['platform_format'] == 'coursera_1' else 'session_user_id'    vars['general_anon_col_name'] = gen_anon    vars['forum_anon_col_name'] = 'forum_user_id' if vars['source']['platform_format'] == 'coursera_1' else 'user_id'    vars['hash_map'] = GetCourseraHashMap(vars, gen_anon)        return vars