from ...utilities import dbfrom common import *from openedx_resource_tree import *def AddPlatformSpecificVars(vars):    vars['source']['course_id'] = "{}/{}/{}".format(vars['source']['course_org'], vars['source']['course_id_1'], vars['source']['course_id_2'])                # Get the list of resources from the CMS    # -----------------------------------------    vars['resource_list'] = GetResourceList(vars)            # We will need to get the list of staff, instructors, and students as early as here    # -----------------------------------------------------------------------------------    vars['staff_users'] = GetStaffUsers(vars)    vars['inst_users'] = GetInstructorUsers(vars)    vars['student_users'] = GetStudentUsers(vars)        return vars