def KeyExistsValueNonEmpty(d, keys):    for k in keys:        if k not in d.keys():            raise Exception("Error: Key \"{}\" missing from an input parameter".format(k))        elif d[k] == None or d[k] == '':            raise Exception("Error: Key \"{}\" in one of the input parameters had an unacceptable empty value".format(k))            def KeyExists(d, keys):    for k in keys:        if k not in d.keys():            raise Exception("Error: Key \"{}\" missing from an input parameter".format(k))